import React, { useState, useEffect,useRef } from 'react';
import './App.css';
import { 
  TextareaAutosize,
  TextField,
  Button,
  FormControl,
  Select,
  MenuItem
} from '@material-ui/core';
import HackerEarth from './ide'



function App() {
  // eslint-disable-next-line

  const [lang, setLang] = useState([
    {
      name:"CPP",
      v:1
    },
    {
      name:"PYTHON",
      v:2
    }
  ],[])

  const [curr, setCurr] = useState([0])
  const [sourcecode,setSource]=useState({
    value:''
  })
  const [input,setInput]=useState({
    value:''
  })
  const [output,setOutput]=useState({
    value:''
  })
  const codeRef = React.createRef()
  const stdinRef = useRef()
  const handleSubmit = async(e) => {
    e.preventDefault();
    console.log(codeRef.current.value)
    await setSource({ value: `${codeRef.current.value}`})

    console.log(sourcecode.value)
     setInput({ 
       value: `${stdinRef.current.value}`
      })

      const hackerEarth = new HackerEarth(
        '89d6d425a2229325e68ae2bbe0c12d49dcd76a63', //client secret key
        ''
    );



    const config = {};

    config.time_limit = 1;

    config.memory_limit = 323244;

    config.source = `${sourcecode}`; 

    config.input = `${input}`;

    config.lang=`${lang[curr].name}`



    hackerEarth.run(config, (err, res) => {
        if (err) {
          console.log(`${lang[curr].name}`)
            console.log(err);
        }
        console.log(JSON.parse(res).run_status.output);
        setOutput({
          value:JSON.parse(res).run_status.output
        })
    });
  };


  const onLangChange=async(event)=>{
    const valueIndex= event.target.value
     setCurr(valueIndex)
    console.log(valueIndex)
  }



  return (
    <div className="App">
      <div className="App__left">
      <FormControl className="App__dropdown">
        <Select
        className="App__select"
        variant="outlined"
        value={curr}
        style={{backgroundColor:"yellow", color:"black"}}
        onChange={onLangChange}
        >
          <MenuItem style={{}} value={0}>{lang[0].name}</MenuItem>
          <MenuItem style={{}} value={1}> {lang[1].name}</MenuItem>

        </Select>
    </FormControl>
      <input type="text" ref={codeRef} />


      <Button variant="contained" color="primary" onClick={handleSubmit}>Run</Button>
      <Button variant="contained" color="secondary">Compile</Button>
      <Button variant="contained" color="primary">Clear</Button>

      </div>
      <div className="App__right">
      <input type="text" ref={stdinRef}  />
      <TextareaAutosize value={output.value}></TextareaAutosize>
      </div>

    </div>
  );
}

export default App;
