import React, { useState, useEffect,useRef } from 'react';
import './App.css';
import { 
  TextareaAutosize,
  TextField,
  Button,
  FormControl,
  Select,
  MenuItem
} from '@material-ui/core';
import HackerEarth from './ide'



function App() {
  // eslint-disable-next-line

  const [lang, setLang] = useState([
    {
      name:"C++",
      v:1
    },
    {
      name:"Python",
      v:2
    }
  ],[])

  const [curr, setCurr] = useState([1])
  const [source,setSource]=useState({
    value:''
  })
  const [input,setInput]=useState({
    value:''
  })
  const [output,setOutput]=useState({
    value:''
  })
  const codeRef = React.createRef()
  const stdinRef = useRef()
  const handleSubmit = async(e) => {
    e.preventDefault();
    console.log(codeRef)
    await setSource({ value: codeRef.current.value})
    console.log(source)
    console.log(stdinRef)
     setInput({ value: stdinRef.current.value})
     var CLIENT_SECRET = "89d6d425a2229325e68ae2bbe0c12d49dcd76a63";
     var proxyUrl = 'https://cors-anywhere.herokuapp.com/'
     var targetUrl='https://api.hackerearth.com/v3/code/run/'
     const api=`${proxyUrl}https://api.hackerearth.com/v3/code/run/`
     console.log(source)
     const requestOptions = {
      method: 'POST',
      headers:{
        'Content-Type': 'multipart/form-data',
        'X-Requested-With':'XMLHttpRequest',
        'Access-Control-Allow-Origin':'http://localhost:3000',
        'Access-Control-Allow-Methods':'GET, PUT, POST, DELETE, OPTIONS',
        'Access-Control-Allow-Headers':'Accept, Content-Type, Origin'
      },
      form:{
        'client_secret': "89d6d425a2229325e68ae2bbe0c12d49dcd76a63",
        'async': 0,
        'source':source ,
        'lang': curr,
        'time_limit': 5,
        'memory_limit': 262144,
      }
    }
      //fetch(api,requestOptions)
     // .then((response)=>response.json())
      //.then((data)=>console.log(data))
      const hackerEarth = new HackerEarth(
        '89d6d425a2229325e68ae2bbe0c12d49dcd76a63', //client secret key
        ''
    );



    const config = {};

    config.time_limit = 1;

    config.memory_limit = 323244;

    config.source = `#include <iostream>

    using namespace std;

    int main()
    {
        cout<<"Hello World";

        return 0;
    }
    `; 

    config.input = "5";

    config.lang=



    hackerEarth.run(config, (err, res) => {
        if (err) {
            console.log(err);
        }
        console.log(JSON.parse(res));
    });
  };


  const onLangChange=async(event)=>{
    const valueIndex= event.target.value
     setCurr(valueIndex)
    console.log(valueIndex)
  }



  return (
    <div className="App">
      <div className="App__left">
      <FormControl className="App__dropdown">
        <Select
        className="App__select"
        variant="outlined"
        value={curr}
        style={{backgroundColor:"yellow", color:"black"}}
        onChange={onLangChange}
        >
          <MenuItem style={{}} value={1}>{lang[0].name}</MenuItem>
          <MenuItem style={{}} value={2}> {lang[1].name}</MenuItem>

        </Select>
    </FormControl>
      <input type="text" ref={codeRef} />
      <input type="text" ref={stdinRef}  />

      <Button variant="contained" color="primary" onClick={handleSubmit}>Run</Button>
      <Button variant="contained" color="secondary">Compile</Button>
      <Button variant="contained" color="primary">Clear</Button>

      </div>
      <div className="App__right">

      <TextareaAutosize value={output.value}></TextareaAutosize>
      </div>

    </div>
  );
}

export default App;
