import React, { useState, useEffect,useRef } from 'react';
import './App.css';
import { 

  TextField,
  Button,
  FormControl,
  Select,
  MenuItem
} from '@material-ui/core';


function App() {
  // eslint-disable-next-line
  const [lang, setLang] = useState([
    {
      name:"C++",
      v:1
    },
    {
      name:"Python",
      v:2
    }
  ],[])

  const [curr, setCurr] = useState([1])
  const [source,setSource]=useState([])
  const inputRef = useRef(null);

  const onLangChange=async(event)=>{
    const valueIndex= event.target.value
     setCurr(valueIndex)
    console.log(valueIndex)
  }

  const onRunBtn=()=>{
   inputRef.current.focus()
   inputRef.current.click()
   inputRef.current.value="myname"
   console.log(inputRef.current.value)

  }

  return (
    <div className="App">
      <div className="App__left">
      <FormControl className="App__dropdown">
        <Select
        className="App__select"
        variant="outlined"
        value={curr}
        style={{backgroundColor:"yellow", color:"black"}}
        onChange={onLangChange}
        >
          <MenuItem style={{}} value={1}>{lang[0].name}</MenuItem>
          <MenuItem style={{}} value={2}> {lang[1].name}</MenuItem>

        </Select>
      </FormControl>
      <FormControl><TextField ref={inputRef} className="Main-code" id="outlined-multiline-flexible"
          label="Multiline"
          multiline
          rowsMax={4} ></TextField>
          <Button variant="contained" color="primary" onClick={onRunBtn}>Run</Button>
      <Button variant="contained" color="secondary">Compile</Button>
      <Button variant="contained" color="primary">Clear</Button>
      </FormControl>      


      </div>
      <div className="App__right">
      <TextField className="Main-code" abel="Multiline"
          multiline
          rowsMax={4}></TextField>
      </div>

    </div>
  );
}

export default App;
