import React, { useState, useEffect,useRef } from 'react';
import './App.css';
import { 
  TextareaAutosize,
  TextField,
  Button,
  FormControl,
  Select,
  MenuItem
} from '@material-ui/core';



function App() {
  // eslint-disable-next-line

  const [lang, setLang] = useState([
    {
      name:"C++",
      v:1
    },
    {
      name:"Python",
      v:2
    }
  ],[])

  const [curr, setCurr] = useState([1])
  const [source,setSource]=useState({
    value:''
  })
  const [input,setInput]=useState({
    value:''
  })
  const [output,setOutput]=useState({
    value:''
  })
  const codeRef = React.createRef()
  const stdinRef = useRef()
  const handleSubmit = async(e) => {
    e.preventDefault();
    console.log(codeRef)
    await setSource({ value: codeRef.current.value})
    console.log(source)
    console.log(stdinRef)
     setInput({ value: stdinRef.current.value})
     var CLIENT_SECRET = "89d6d425a2229325e68ae2bbe0c12d49dcd76a63";
     var proxyUrl = 'https://cors-anywhere.herokuapp.com/'
     var targetUrl='https://api.hackerearth.com/v3/code/run/'
     const requestOptions = {
      method: 'POST',
      headers:{
        'Content-Type': 'multipart/form-data',

      },
      form:{
        'client_secret': "89d6d425a2229325e68ae2bbe0c12d49dcd76a63",
        'async': 0,
        'source':`${source}` ,
        'lang': `${curr}`,
        'time_limit': 5,
        'memory_limit': 262144,
      }
    }
      fetch(proxyUrl+targetUrl,requestOptions)
      //.then((response)=>JSON.parse(response))
      .then((data)=>console.log(data))
  };


  const onLangChange=async(event)=>{
    const valueIndex= event.target.value
     setCurr(valueIndex)
    console.log(valueIndex)
  }



  return (
    <div className="App">
      <div className="App__left">
      <FormControl className="App__dropdown">
        <Select
        className="App__select"
        variant="outlined"
        value={curr}
        style={{backgroundColor:"yellow", color:"black"}}
        onChange={onLangChange}
        >
          <MenuItem style={{}} value={1}>{lang[0].name}</MenuItem>
          <MenuItem style={{}} value={2}> {lang[1].name}</MenuItem>

        </Select>
    </FormControl>
      <input type="text" ref={codeRef} />
      <input type="text" ref={stdinRef}  />

      <Button variant="contained" color="primary" onClick={handleSubmit}>Run</Button>
      <Button variant="contained" color="secondary">Compile</Button>
      <Button variant="contained" color="primary">Clear</Button>

      </div>
      <div className="App__right">

      <TextareaAutosize value={output.value}></TextareaAutosize>
      </div>

    </div>
  );
}

export default App;
