import React, { useState, useEffect,useRef } from 'react';
import './App.css';
import { 
  TextareaAutosize,
  TextField,
  Button,
  FormControl,
  Select,
  MenuItem
} from '@material-ui/core';



function App() {
  // eslint-disable-next-line
  {var hackerEarth=require('hackerearth-node'); //require the Library
  //Now set your application 
  var hackerEarth=new hackerEarth(
                                  '89d6d425a2229325e68ae2bbe0c12d49dcd76a63',  //Your Client Secret Key here this is mandatory
                                  ''  //mode sync=1 or async(optional)=0 or null async is by default and preferred for nodeJS
  );

  function onCompile(){
    var config={};
  config.time_limit=1;  //your time limit in integer
  config.memory_limit=323244;  //your memory limit in integer
  config.source=`#include <iostream>

  using namespace std;

  int main()
  {
      cout<<"Hello World";

      return 0;
  }
  `;  //your source code for which you want to use hackerEarth api
  config.input="";  //input against which you have to test your source code
  config.language="C++"; //optional choose any one of them or none
  hackerEarth.compile(config)
                          .then(result => {
                            //Handle Result

                            let ans=JSON.parse(result)
                            console.log(ans.compile_status)
                          })
                          .catch(err => {
                            //Handle Error
                            console.log(err)
                          });
    }
    onRun=()=>{
      var config={};
  config.time_limit=1;  //your time limit in integer
  config.memory_limit=323244;  //your memory limit in integer
  config.source=`#include <iostream>

  using namespace std;

  int main()
  {
      cout<<"Hello World";

      return 0;
  }
  `;  //your source code for which you want to use hackerEarth api
  config.input="";  //input against which you have to test your source code
  config.language="C++"; //optional choose any one of them or none
   hackerEarth.run(config)
                      .then(result => {
                        //Handle Result
                        let ans=JSON.parse(result)
                            console.log(ans.run_status.output)
                      })
                      .catch(err => {
                        //Handle Error
                      });      
                    }
  }
  const [lang, setLang] = useState([
    {
      name:"C++",
      v:1
    },
    {
      name:"Python",
      v:2
    }
  ],[])

  const [curr, setCurr] = useState([1])
  const [source,setSource]=useState({
    value:''
  })
  const [input,setInput]=useState({
    value:''
  })
  const [output,setOutput]=useState({
    value:''
  })
  const codeRef = React.createRef()
  const stdinRef = useRef()
  const handleSubmit = async(e) => {
    e.preventDefault();
    console.log(codeRef)
    await setSource({ value: codeRef.current.value})
    console.log(source)
    console.log(stdinRef)
     setInput({ value: stdinRef.current.value})


  };

  const onLangChange=async(event)=>{
    const valueIndex= event.target.value
     setCurr(valueIndex)
    console.log(valueIndex)
  }



  return (
    <div className="App">
      <div className="App__left">
      <FormControl className="App__dropdown">
        <Select
        className="App__select"
        variant="outlined"
        value={curr}
        style={{backgroundColor:"yellow", color:"black"}}
        onChange={onLangChange}
        >
          <MenuItem style={{}} value={1}>{lang[0].name}</MenuItem>
          <MenuItem style={{}} value={2}> {lang[1].name}</MenuItem>

        </Select>
    </FormControl>
      <input type="text" ref={codeRef} />
      <input type="text" ref={stdinRef}  />

      <Button variant="contained" color="primary" onClick={handleSubmit}>Run</Button>
      <Button variant="contained" color="secondary">Compile</Button>
      <Button variant="contained" color="primary">Clear</Button>

      </div>
      <div className="App__right">

      <TextareaAutosize value={output.value}></TextareaAutosize>
      </div>

    </div>
  );
}

export default App;
