import React, { useState, useEffect } from 'react';
import './App.css';
import { 

  TextareaAutosize,
  Button,
  FormControl,
  Select,
  MenuItem
} from '@material-ui/core';


function App() {
  // eslint-disable-next-line
  const [lang, setLang] = useState([
    {
      name:"C++",
      v:1
    },
    {
      name:"Python",
      v:2
    }
  ],[])

  const [curr, setCurr] = useState([])

  const onLangChange=async(event)=>{
    const valueIndex= event.target.value
     setCurr(valueIndex)
    console.log(valueIndex)
  }

  const onRunBtn=()=>{
  }
  return (
    <div className="App">
      <div className="App__left">
      <FormControl className="App__dropdown">
        <Select
        className="App__select"
        variant="outlined"
        value={1}
        style={{backgroundColor:"yellow", color:"black"}}
        onChange={onLangChange}
        >
          <MenuItem style={{}} value={1}>{lang[0].name}</MenuItem>
          <MenuItem style={{}} value={2}> {lang[1].name}</MenuItem>

        </Select>
      </FormControl>
      <TextareaAutosize className="Main-code" aria-label="minimum height" rowsMin={20} placeholder="Minimum 3 rows"></TextareaAutosize>
      <Button variant="contained" color="primary" onClick={onRunBtn}>Run</Button>
      <Button variant="contained" color="secondary">Compile</Button>
      <Button variant="contained" color="secondary">Clear</Button>
      </div>
      <div className="App__right">
      <TextareaAutosize className="Main-code" aria-label="minimum height" rowsMin={3} placeholder="Minimum 3 rows"></TextareaAutosize>
      </div>

    </div>
  );
}

export default App;
