import React, { useState, useEffect,useRef } from 'react';
import './App.css';
import { 
  ThemeProvider,
  Paper,
  createMuiTheme,
  Button,
  FormControl,
  Select,
  MenuItem
} from '@material-ui/core';
import HackerEarth from './ide'



function App() {
  // eslint-disable-next-line
  const [darkMode, setDarkMode] = React.useState(getInitialMode());
  React.useEffect(() => {
    localStorage.setItem("light", JSON.stringify(darkMode));
  }, [darkMode]);

  function getInitialMode() {
    const isReturningUser = "light" in localStorage;
    const savedMode = JSON.parse(localStorage.getItem("light"));
    const userPrefersDark = getPrefColorScheme();
    // if mode was saved --> dark / light
    if (isReturningUser) {
      return savedMode;
      // if preferred color scheme is dark --> dark
    } else if (userPrefersDark) {
      return true;
      // otherwise --> light
    } else {
      return false;
    } 
    // return savedMode || false;
  }

  function getPrefColorScheme() {
    if (!window.matchMedia) return;

    return window.matchMedia("(prefers-color-scheme: dark)").matches;
  }
  const theme= createMuiTheme({
    palette:{
      type: darkMode ? "dark" : "light"
      ,
    },
  });
  const [lang, setLang] = useState([
    {
      name:"CPP",
      v:1
    },
    {
      name:"PYTHON",
      v:2
    }
  ],[])

  const [curr, setCurr] = useState(0)

  const [output,setOutput]=useState({
    value:''
  })
  const codeRef = React.createRef()
  const stdinRef = useRef()
  const handleSubmit = async(e) => {
    e.preventDefault();
    console.log(`${codeRef.current.value}`)
    const s=`${codeRef.current.value}`
    //await setSource( `${codeRef.current.value}`)
    await console.log(s)
   // await console.log(typeof(sourcecode))
   const i=`${stdinRef.current.value}`



      const hackerEarth = new HackerEarth(
        '89d6d425a2229325e68ae2bbe0c12d49dcd76a63', //client secret key
        ''
    );


    const config = {};

    config.time_limit = 1;

    config.memory_limit = 323244;

    config.source = s; 

    config.input = i;

    config.lang=`${lang[curr].name}`



    hackerEarth.run(config, (err, res) => {
        if (err) {
          console.log(`${lang[curr].name}`)
            console.log(err);
        }
        else{
        //console.log(config.source)
        //console.log(res)
        if(JSON.parse(res).compile_status!=="OK"){
          setOutput({
            value:"Your code had an error: "+JSON.parse(res).compile_status
          })
        }
        console.log(JSON.parse(res));
        setOutput({
          value:JSON.parse(res).run_status.output
        })
      }
    });


  };
  const handleSubmit2 = async(e) => {
    e.preventDefault();
    console.log(`${codeRef.current.value}`)
    const s=`${codeRef.current.value}`
    //await setSource( `${codeRef.current.value}`)
    await console.log(s)
   // await console.log(typeof(sourcecode))
   const i=`${stdinRef.current.value}`



      const hackerEarth = new HackerEarth(
        '89d6d425a2229325e68ae2bbe0c12d49dcd76a63', //client secret key
        ''
    );


    const config = {};

    config.time_limit = 1;

    config.memory_limit = 323244;

    config.source = s; 

    config.input = i;

    config.lang=`${lang[curr].name}`



    hackerEarth.compile(config, (err, res) => {
      if (err) {
        console.log(`${lang[curr].name}`)
          console.log(err);
      }
      else{
      //console.log(config.source)
      //console.log(res)
      if(JSON.parse(res).compile_status!=="OK"){
        setOutput({
          value:"Your code had an error: "+JSON.parse(res).compile_status
        })
      }
      else{
        setOutput({
          value:JSON.parse(res).compile_status
        })
      }
      }
    });


  };

  const onLangChange=async(event)=>{
    const valueIndex= event.target.value
     setCurr(valueIndex)
    console.log(valueIndex)
  }



  return (
    <ThemeProvider   theme={theme} > 
       <nav className={darkMode? "dark-mode":"light-mode"}>

      <div className="toggle-containerr">
      <div className="App__top">
           <h1>ONLINE IDE:</h1>
         </div>
          <span style={{ color: darkMode ? "#52606D" : "yellow" }}>☀︎</span>
          <span className="togglee">
            <input
              checked={darkMode}
              onChange={() => {setDarkMode(prevMode => !prevMode) }}
              id="checkbox"
              className="checkbox"
              type="checkbox"
            />
            <label htmlFor="checkbox" />
          </span>
          <span style={{ color: darkMode ? "#E8E6E1" : "#616e7c" }}>☾</span>
        </div>
        </nav>
     <Paper  className= "app__paper" style={{height:"100%", backgroundColor:darkMode? "#212121" : "gainsboro"}}>
    <div className="App">
    <div className="App__left">
      <div className="App__header">
      <h1>Select Language: </h1>
    <FormControl className="App__dropdown">
      <Select
      className="App__select"
      variant="outlined"
      value={curr}
      style={{backgroundColor:darkMode? "#857F72": "white", color:darkMode? "yellow":"black"}}
      onChange={onLangChange}
      >
        <MenuItem style={{}} value={0}>{lang[0].name}</MenuItem>
        <MenuItem style={{}} value={1}> {lang[1].name}</MenuItem>

      </Select>
  </FormControl>
      </div>
      <h3>Code Editor: </h3>
    <textarea type="text" ref={codeRef} className="App__code"></textarea>
    <div className="App_buttons">
    <Button variant="contained" color="primary"  style={{"margin-right": "10px"}} onClick={handleSubmit}>Run</Button>
    <Button variant="contained" color="secondary" style={{"margin-right": "10px"}} onClick={handleSubmit2}>Compile</Button>
    <Button variant="contained" color="primary">Clear</Button>
    </div>
    </div>
    <div className="App__right">
    <textarea type="text" ref={stdinRef} className="App__input" />
    <textarea value={output.value} className="App__output" ></textarea>
    </div>

  </div>
  </Paper>
  </ThemeProvider>

  );
}

export default App;
