import React, { useState, useEffect,useRef } from 'react';
import './App.css';
import { 
  Button,
  FormControl,
  Select,
  MenuItem
} from '@material-ui/core';
import HackerEarth from './ide'



function App() {
  // eslint-disable-next-line

  const [lang, setLang] = useState([
    {
      name:"CPP",
      v:1
    },
    {
      name:"PYTHON",
      v:2
    }
  ],[])

  const [curr, setCurr] = useState(0)

  const [output,setOutput]=useState({
    value:''
  })
  const codeRef = React.createRef()
  const stdinRef = useRef()
  const handleSubmit = async(e) => {
    e.preventDefault();
    console.log(`${codeRef.current.value}`)
    const s=`${codeRef.current.value}`
    //await setSource( `${codeRef.current.value}`)
    await console.log(s)
   // await console.log(typeof(sourcecode))
   const i=`${stdinRef.current.value}`



      const hackerEarth = new HackerEarth(
        '89d6d425a2229325e68ae2bbe0c12d49dcd76a63', //client secret key
        ''
    );


    const config = {};

    config.time_limit = 1;

    config.memory_limit = 323244;

    config.source = s; 

    config.input = i;

    config.lang=`${lang[curr].name}`



    hackerEarth.run(config, (err, res) => {
        if (err) {
          console.log(`${lang[curr].name}`)
            console.log(err);
        }
        else{
        //console.log(config.source)
        //console.log(res)
        if(JSON.parse(res).compile_status!=="OK"){
          setOutput({
            value:JSON.parse(res).compile_status
          })
        }
        console.log(JSON.parse(res));
        setOutput({
          value:JSON.parse(res).run_status.output
        })
      }
    });


  };
  const handleSubmit2 = async(e) => {
    e.preventDefault();
    console.log(`${codeRef.current.value}`)
    const s=`${codeRef.current.value}`
    //await setSource( `${codeRef.current.value}`)
    await console.log(s)
   // await console.log(typeof(sourcecode))
   const i=`${stdinRef.current.value}`



      const hackerEarth = new HackerEarth(
        '89d6d425a2229325e68ae2bbe0c12d49dcd76a63', //client secret key
        ''
    );


    const config = {};

    config.time_limit = 1;

    config.memory_limit = 323244;

    config.source = s; 

    config.input = i;

    config.lang=`${lang[curr].name}`



    hackerEarth.compile(config, (err, res) => {
        if (err) {
          console.log(`${lang[curr].name}`)
            console.log(err);
            setOutput({
              value:JSON.parse(err).compile_status
            })
        }
       else{
        console.log(JSON.parse(res));

        setOutput({
          value:JSON.parse(res).compile_status
        })
      }
    });


  };

  const onLangChange=async(event)=>{
    const valueIndex= event.target.value
     setCurr(valueIndex)
    console.log(valueIndex)
  }



  return (
    <div className="App">
      <div className="App__left">
      <FormControl className="App__dropdown">
        <Select
        className="App__select"
        variant="outlined"
        value={curr}
        style={{backgroundColor:"yellow", color:"black"}}
        onChange={onLangChange}
        >
          <MenuItem style={{}} value={0}>{lang[0].name}</MenuItem>
          <MenuItem style={{}} value={1}> {lang[1].name}</MenuItem>

        </Select>
    </FormControl>

      <textarea type="text" ref={codeRef} className="App__code"></textarea>
      <div className="App_buttons">
      <Button variant="contained" color="primary" style={{"margin-right": "10px"}} onClick={handleSubmit}>Run</Button>
      <Button variant="contained" color="secondary" style={{"margin-right": "10px"}} onClick={handleSubmit2}>Compile</Button>
      <Button variant="contained" color="primary">Clear</Button>
      </div>
      </div>
      <div className="App__right">
      <textarea type="text" ref={stdinRef} className="App__input" />
      <textarea value={output.value} className="App__output" ></textarea>
      </div>

    </div>
  );
}

export default App;
